import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';


import Header from './Header';
import Github from './Github';
import Spinner from '../../utils/Spinner';

import { getProfile, getRepositories } from '../../actions/profileActions';


class Profile extends Component {

    componentDidMount() {
        const { userName } = this.props.profile;
        this.props.getProfile(userName);
        this.props.getRepositories(userName);
    }

     /*componentWillReceiveProps(nextProps) {
        if (nextProps.errors) {
            this.props.history.push("/");
        }
    }*/

    render() {

        const { profile, repos, loading, userName } = this.props.profile;
        let profileContent;

        if (loading) {
            profileContent = <Spinner />
        }
        else {
            profile.login !== "undefined" &&  userName!== "" ? (
                profileContent = (
                    <div>
                        <Header profile={profile} />
                        <Github repos={repos} />
                    </div>
                )) : //Profile is undefined. Back to search page
                this.props.history.push("/");
        }
        return (
            <div className="profile">
                <div className="container">
                    <div className="row">
                        <div className="col-md-12">
                            {profileContent}
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}


Profile.propTypes = {
    getProfile: PropTypes.func.isRequired,
    getRepositories: PropTypes.func.isRequired,
    profile: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired,
}

const mapStateToProps = state => ({
    profile: state.profile,
    errors: state.errors
});

export default connect(mapStateToProps, { getProfile, getRepositories })(Profile);
